import React, {useState} from 'react';
import img from '../images/pattern-bg.png';
import arrow from '../images/icon-arrow.svg';
import DataDisplay from './DataDisplay';

function Banner() {
  const [ipAddress, setIpAddress] = useState(null);

  function handleClick(event) {
    setIpAddress(event.target.value)
  }

  return (<div className='banner' style={{ backgroundImage:`url(${img})` , backgroundRepeat: 'no-repeat'}}>
      <h1 className='title'>IP Address Tracker</h1>
    <div className='input-form'>
      <input id={ip} type="text" className='input-field' placeholder='Search for any IP address or domain'/>
      <input id={ip} type="image" alt='submit button' src={arrow} className='input-btn'  onClick={handleClick} />
    </div>
    <DataDisplay key={ip} ipAddress={ipAddress}/>
  </div>
  )
}


export default Banner
