import React, {useState} from 'react';
import img from '../images/pattern-bg.png';
import arrow from '../images/icon-arrow.svg';
import DataDisplay from './DataDisplay';
import Map from './Map';
import useFetch from '../hooks/useFetch';

// TODO add onSubmit handler function(hook) to  form element too search ip address

function Banner() {
  // const [ipAddress, setIpAddress] = useState('');

  const { data, loading, error } = useFetch('https://geo.ipify.org/api/v2/country?apiKey=at_8GE1HPonrAAGY0ggGjL3YAuoxACZ1&ipAddress=8.8.8.8');

  if (loading) return <h1>LOADING...</h1>;

  if (error) console.log(error);

  return (
    <>
      <div className='banner' style={{ backgroundImage: `url(${img})`, backgroundRepeat: 'no-repeat' }}>
        <h1 className='title'>IP Address Tracker</h1>
        <form className='input-form'>
          <input
            type="text"
            value={ipAddress}
            onChange={(e)=>{setIpAddress(e.target.value)}} 
            className='input-field'
            placeholder='Search for any IP address or domain' />
          <input
            type="image"
            alt='submit button'
            src={arrow} className='input-btn' />
        </form>
        <DataDisplay key={ipAddress} ipData={ data} />
          <Map />
      </div>
    </>
  )
}

export default Banner
